import React, { useState } from "react";
//import { useNavigate } from "react-router-dom";
import {
  Main,
  Fieldset,
  InputField,
  Button,
  InsetText,
  Link,
} from "govuk-react";

function LoginForm() {
  //const navigate = useNavigate();

  const [email, setEmail] = useState(" ");
  const [password, setPassword] = useState(" ");

  const [validEmail, setValidEmail] = useState(false);

  const submitFormHandler = (e) => {
    e.preventDefault();
  };

  const emailHandler = (e) => {
    const input = e.target.value;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (emailRegex.test(input) && email.length > 5) {
      setEmail(input);
      setValidEmail(true);
    } else {
      setValidEmail(false);
    }
  };

  return (
    <div>
      <Main>
        <form onSubmit={submitFormHandler}>
          <Fieldset>
            <Fieldset.Legend size="LARGE">Login</Fieldset.Legend>
            <InputField
              padding={3}
              onChange={emailHandler}
              value={email}
              input={{
                name: "useremail",
                type: "email",
              }}
            >
              Email address
            </InputField>
            <InputField
              padding={3}
              onChange={(e) => setPassword(e.target.value)}
              value={password}
              input={{
                name: "userpassword",
                type: "password",
              }}
            >
              Password
            </InputField>
            <Button type="submit" margin={3} disabled={!validEmail}>
              Continue
            </Button>
          </Fieldset>
        </form>

        <br />
        <InsetText margin={3}>
          New patient? Register&nbsp;
          <Link href="/NhsNumber">here</Link>
        </InsetText>
        <InsetText margin={3}>
          Staff login&nbsp;
          <Link href="#">here</Link>
        </InsetText>
        <br />
        <br />
      </Main>
    </div>
  );
}

export default LoginForm;
