import React, { useState } from "react";
import { DateField, Main, Button } from "govuk-react";
import { useNavigate } from "react-router-dom";
import Navigation from "../../../Navigation";

function DOBChangeEnter() {
  const navigate = useNavigate();
  const [dob, setDOB] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`dob: ${dob}`);

    // check that dob is a valid date
    const parsedDOB = Date.parse(dob);
    if (isNaN(parsedDOB)) {
      alert("Please enter a valid date of birth.");
      return;
    }
    const newDOB = new Date(parsedDOB);
    console.log(`newDOB: ${newDOB}`);

    // format the dob for display
    const formattedDOB = `${newDOB.getDate()}/${
      newDOB.getMonth() + 1
    }/${newDOB.getFullYear()}`;

    // navigate to the next page with formatted dob
    navigate("/confirmation", {
      state: { dob: formattedDOB },
    });
  };

  const handleInputChange = (e) => {
    setDOB(e.target.value);
  };

  return (
    <div>
      <Navigation
        pageLink1="/"
        PageName1="home"
        pageLink2="/login"
        PageName2="Login"
        pageLink3="/DOBChangeEnter"
        PageName3="Register"
      />

      <Main>
        <form onSubmit={handleSubmit}>
          <DateField
            input={{
              onBlur: function noRefCheck() {},
              onChange: handleInputChange,
              onFocus: function noRefCheck() {},
            }}
            inputNames={{
              day: "dayInputName",
              month: "monthInputName",
              year: "yearInputName",
            }}
            legend="What is your date of birth?"
            hint="For example, 31 03 1980"
          />
          <Button type="submit" start>
            Save and Continue
          </Button>
        </form>
      </Main>
    </div>
  );
}

export default DOBChangeEnter;
