import { Tabs, H2 } from 'govuk-react';
import { useState } from 'react';
import BookTab from './BookAppointment/BookTab';
import DRTab from './De-Register/DRTab';
import DoctorTab from './DoctorsAvailable/DoctorTab';
import ViewTab from './ViewAppointment/ViewTab';
import { useNavigate, useParams } from 'react-router-dom';

function GPTabs() {
  const [selectedTab, setSelectedTab] = useState(0);

  const handleTabChange = (index) => {
    setSelectedTab(index);
  };

  return (
    <div>
      <Tabs>
        <Tabs.Title>Contents</Tabs.Title>
        <Tabs.List>
          <Tabs.Tab
            selected={selectedTab === 0}
            onClick={() => handleTabChange(0)}
          >
            Doctors at GP
          </Tabs.Tab>
          <Tabs.Tab
            selected={selectedTab === 1}
            onClick={() => handleTabChange(1)}
          >
            Book Appointment
          </Tabs.Tab>
          <Tabs.Tab
            selected={selectedTab === 2}
            onClick={() => handleTabChange(2)}
          >
            View/Cancel Appointment
          </Tabs.Tab>
          <Tabs.Tab
            selected={selectedTab === 3}
            onClick={() => handleTabChange(3)}
          >
            De-Register
          </Tabs.Tab>
        </Tabs.List>

        <Tabs.Panel hidden={selectedTab !== 0}>
          <DoctorTab />
        </Tabs.Panel>

        <Tabs.Panel hidden={selectedTab !== 1}>
          <BookTab />
        </Tabs.Panel>

        <Tabs.Panel hidden={selectedTab !== 2} id="2">
          <H2>Past month</H2>
          <Table
            head={
              <Table.Row>
                <Table.CellHeader>Case manager</Table.CellHeader>
                <Table.CellHeader>Cases opened</Table.CellHeader>
                <Table.CellHeader>Cases closed</Table.CellHeader>
              </Table.Row>
            }
          >
            <Table.Row>
              <Table.Cell>David Francis</Table.Cell>
              <Table.Cell>98</Table.Cell>
              <Table.Cell>95</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>Paul Farmer</Table.Cell>
              <Table.Cell>122</Table.Cell>
              <Table.Cell>133</Table.Cell>
            </Table.Row>
            <Table.Row>
              <Table.Cell>Rita Patel</Table.Cell>
              <Table.Cell>126</Table.Cell>
              <Table.Cell>142</Table.Cell>
            </Table.Row>
          </Table>
        </Tabs.Panel>

        <Tabs.Panel hidden={selectedTab !== 3}>
          <DRTab />
        </Tabs.Panel>
      </Tabs>
    </div>
  );
}

export default GPTabs;
