/**
* Author(s) of this code: 
*
* Joven Manikiza
*/

import { Button, Heading, Main, Table, SectionBreak } from "govuk-react";
import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Navigation from "../../../Navigation";

function BookConfirm() {
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [selectedDoctor, setSelectedDoctor] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    setSelectedDoctor(location.state?.selectedDoctor || "");
    setSelectedDate(location.state?.selectedDate || "");
    setSelectedTime(location.state?.selectedTime || "");
  }, [location.state]);

  const handleClick = () => {
    // Perform any action needed on confirmation
    navigate("/Application");
  };

  return (
    <div>
      <Navigation
        pageLink1="/"
        PageName1="home"
        pageLink2="/login"
        PageName2="Login"
        pageLink3="/NhsNumber"
        PageName3="Register"
      />

      <Main>
        <Heading>Confirm your booking</Heading>

        <Heading size="MEDIUM">Your booking is:</Heading>

        <SectionBreak />

        <Table>
          <Table.Row>
            <Table.CellHeader>Doctor:</Table.CellHeader>
            <Table.Cell>{selectedDoctor}</Table.Cell>
          </Table.Row>
          <Table.Row>
            <Table.CellHeader>Date:</Table.CellHeader>
            <Table.Cell>{selectedDate}</Table.Cell>
          </Table.Row>
          <Table.Row>
            <Table.CellHeader>Time:</Table.CellHeader>
            <Table.Cell>{selectedTime}</Table.Cell>
          </Table.Row>
        </Table>

        <Button onClick={handleClick}>Confirm</Button>

        <Button onClick={() => navigate("/GPServices")} buttonColour="GREY">
          Cancel
        </Button>
      </Main>
    </div>
  );
}

export default BookConfirm;
