/**
 * Author(s) of this code:
 *
 * Joven Manikiza
 */

import React, { useState } from "react";
import { DateField, Main, Button, SectionBreak } from "govuk-react";
import { useNavigate } from "react-router-dom";
import Navigation from "../../../Navigation";

function DOBChangeEnter() {
  const navigate = useNavigate();
  const [dob, setDOB] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check if dob is a valid date
    if (dob) {
      // Split the dob string into day, month, year components
      const parts = dob.split("/");
      // Create a new Date object using the parts
      const dateObject = new Date(parts[2], parts[1] - 1, parts[0]); // year, month (0-based), day

      // Navigate to the confirmation page with the date object as state
      navigate("/DOBChangeConfirmation", { state: { dob: dateObject } });
      console.log(dateObject);
    } else {
      alert("Please enter a valid date of birth.");
    }
  };

  return (
    <div>
      {/* Navigation component */}
      <Navigation
        pageLink1="/"
        PageName1="home"
        pageLink2="/login"
        PageName2="Login"
        pageLink3="/NhsNumber"
        PageName3="Register"
      />

      <Main>
        {/* DOB form */}
        <form onSubmit={handleSubmit}>
          <DateField
            hintText="For example, 31/03/1980"
            input={{
              name: "dob",
              onChange: (value) => setDOB(value),
            }}
            meta={{
              // Display an error message if dob is not a valid date
              touched: true,
              error: !dob ? "Please enter a valid date of birth." : undefined,
            }}
          >
            What is your date of birth?
          </DateField>

          <Button>Continue</Button>

          <SectionBreak level="SMALL" visible={false} />

          {/* Cancel button that navigates to the profile page */}
          <Button
            onClick={() => navigate("/Profile")}
            buttonColour="GREY"
          >
            Cancel
          </Button>
        </form>
      </Main>
    </div>
  );
}

export default DOBChangeEnter;
