import { useState } from "react";
import { H2, Button, ButtonArrow, DateField, MultiChoice, Radio, Select } from "govuk-react";
import { useNavigate } from "react-router-dom";

function BookTab() {

  const navigate = useNavigate();

  const [selectedDate, setSelectedDate] = useState("");

  const [selectedTime, setSelectedTime] = useState("");

  const [selectedDoctor, setSelectedDoctor] = useState("");

  const [isInputFilled, setIsInputFilled] = useState(false);


  function handleDateChange(event) {

    setSelectedDate(event.target.value);

  }


  function handleTimeChange(event) {

    setSelectedTime(event.target.value);

  }


  function handleDoctorChange(event) {

    setSelectedDoctor(event.target.value);


  }


  function handleButtonClick() {

    if (selectedDate !== "" && selectedTime !== "" && selectedDoctor !== "") {

      setIsInputFilled(true);

      navigate("/BookConfirm");

    } else {

      setIsInputFilled(false);

      alert("Please fill all the required fields.");

    }

  }

  return (
  
    <div>
      <br />
      <H2>Book Appointment</H2>
      <DateField
        input={{ onChange: handleDateChange, value: selectedDate }}
        defaultValues={{ day: "28", month: "02", year: "2024" }}
      >
        What date would you like to book the appointment for?
      </DateField>
      <br />
      <MultiChoice
        label="What time of day works for you?"
        hint="We will try our best to accommodate your needs"
   >
<Radio value="Morning" onChange={handleTimeChange}>
Morning
</Radio>
<    Radio value="Noon" onChange={handleTimeChange}>
      Noon
    </Radio>
    <Radio value="Afternoon" onChange={handleTimeChange}>
      Afternoon
    </Radio>
  </MultiChoice>
  <br />
  <Select
    input={{ onChange: handleDoctorChange, value: selectedDoctor, name: "doctors" }}
    label="Which doctor would you prefer?"
  >
    <option value="">Select an option</option>
    <option value="0">GOV.UK elements option 1</option>
    <option value="1">GOV.UK elements option 2</option>
    <option value="2">GOV.UK elements option 3</option>
  </Select>
  <br />
  <Button
    onClick={handleButtonClick}
    icon={<ButtonArrow />}
    start
    disabled={!isInputFilled}
  >
    Book now
  </Button>
</div>

);
}

export default BookTab;
