import React, { useState } from "react";
import {
  Main,
  Fieldset,
  InputField,
  Button,
  InsetText,
  Link,
} from "govuk-react";

function LoginForm() {
  const [email, setEmail] = useState(" ");
  const [password, setPassword] = useState(" ");

  const [validEmail, setValidEmail] = useState(false);

  const submitFormHandler = (e) => {
    e.preventDefault();

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      validEmail(true);
    }
  };

  const emailHandler = (e) => {
    const input = e.target.value;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(input)) {
      setEmail(input);
      validEmail(true);
    } else {
      setEmail('');
      validEmail(false);
    }
    
  }

  return (
    <div>
      <Main>
        <form onSubmit={submitFormHandler}>
          <Fieldset.Legend size="LARGE">Login</Fieldset.Legend>
          <InputField
            padding={3}
            onChange={emailHandler}
            value={email}
            input={{
              name:"useremail",
              type: 'email'
            }} 
            
          >
            Email address
          </InputField>
          <InputField
            padding={3}
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            input={{
              name:"userpassword",
              type: 'password'
            }}  
          >
            Password
          </InputField>
          <Button type="submit" margin={3} disabled={!validEmail}>
            Continue
          </Button>
        </form>

        <br />
        <InsetText margin={3}>
          New patient? Register&nbsp;
          <Link href="#">here</Link>
        </InsetText>
        <br />
      </Main>
    </div>
  );
}

export default LoginForm;
