import { useState, useEffect } from "react";
import { Table, Tabs, H2 } from "govuk-react";

function DoctorTab(){

    const [doctors, setDoctors] = useState([]);

    useEffect(() => {
        fetch('GPS_Doc_Avail.php')
            .then(response => response.json())
            .then(data => {
                setDoctors(data);
            });
    }, []);

    return (
        <div>
            <br/>
            <H2>Doctors Available</H2>
            <Table head={
                <Table.Row>
                    <Table.CellHeader>Name</Table.CellHeader>
                    <Table.CellHeader>Specialisation</Table.CellHeader>
                    <Table.CellHeader>Email</Table.CellHeader>
                    <Table.CellHeader>Contact No.</Table.CellHeader>
                </Table.Row>
            }>
                {doctors.map((doctor) => (
                    <Table.Row key={doctor.id}>
                        <Table.Cell>{doctor.surname}</Table.Cell>
                        <Table.Cell>{doctor.specialisation}</Table.Cell>
                        <Table.Cell>{doctor.email}</Table.Cell>
                        <Table.Cell>{doctor.contact_no}</Table.Cell>
                    </Table.Row>
                ))}
            </Table>
        </div>
    );
}

export default DoctorTab;
