/**
* Author(s) of this code: 
*
* Joven Manikiza
*/

import React, { useState } from "react";
import { DateField, Main, Button, SectionBreak } from "govuk-react";
import { useNavigate } from "react-router-dom";
import Navigation from "../../../Navigation";

function DOBChangeEnter(){

    const navigate = useNavigate();
    const [dob, setDOB] = useState(null);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (dob) {
            const day = dob.getDate().toString().padStart(2, "0"); // Day of the month, padded with leading zeros if necessary
            const month = (dob.getMonth() + 1).toString().padStart(2, "0"); // Month, 0-based (January is 0), padded with leading zeros if necessary
            const year = dob.getFullYear().toString(); // Four-digit year
            const formattedDOB = `${day}/${month}/${year}`;
            navigate("/DOBChangeConfirmation", { state: { dob: formattedDOB } });
            console.log(formattedDOB);
        } else {
            alert("Please enter a valid date of birth.");
        }
    };
    
    return(
        <div>
            <Navigation pageLink1="/" PageName1="home" pageLink2="/login" PageName2="Login" pageLink3="/NhsNumber" PageName3="Register"/>

            <Main>
                
                <form onSubmit={handleSubmit}>
                    <DateField
                        hintText="For example, 31 03 1980"
                        input={{
                          name: "dob",
                          onChange: (value) => setDOB(value),
                        }}
                        meta={{
                          touched: true,
                          error: !dob ? "Please enter a valid date of birth." : undefined,
                        }}
                    >
                        What is your date of birth?
                    </DateField>

                    <Button>
                        Continue
                    </Button>

                    <SectionBreak level="SMALL" visible={false}/>
                    
                    <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
                        Cancel
                    </Button>

                </form>

            </Main>
        </div>

    );
}

export default DOBChangeEnter;
