/**
* Author(s) of this code: 
*
* Joven Manikiza
*/

import { Button, Heading, Main,Table, SectionBreak } from "govuk-react";
import React, {useEffect, useState} from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Navigation from "../../../Navigation";

function NumberChangeConfirmation(){

    const navigate = useNavigate();

    const [data, setData] = useState({});
    const location = useLocation();
    const number = location.state;

    const payload = number;

    useEffect(() => {
        fetch('http://localhost:4000/getData.php')
          .then(response => response.json())
          .then(data => {
            setData({
                previousNumber: data[0].Number,
            });
          })
          .catch(error => console.error(error));
    }, []);

    console.log("Number:", number);

    const updateNumber = () => {
      fetch('http://localhost:4000/updateNumber.php', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({number: payload}),
        credentials: 'include',
      })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to update number');
        }
        navigate('/Application');
      })
      .catch(error => console.error(error));
    };

    return (
        <div>
            <Navigation pageLink1="/" PageName1="home" pageLink2="/login" PageName2="Login" pageLink3="/NhsNumber" PageName3="Register"/>

            <Main>

                <Heading>
                    Confirm your changes
                </Heading>

                <Heading size="MEDIUM">Previous details were:</Heading>
                
                <SectionBreak></SectionBreak>

                <Table>
                    <Table.Row>
                        <Table.CellHeader>
                            Number:
                        </Table.CellHeader>
                        <Table.Cell>
                           {data.previousNumber}
                        </Table.Cell>
                    </Table.Row>
                </Table>
 
                <Heading size="MEDIUM">New details are:</Heading>

                <Table>
                    <Table.Row>
                        <Table.CellHeader>
                            Number:
                        </Table.CellHeader>
                        <Table.Cell>
                            {number}
                        </Table.Cell>
                    </Table.Row>
                </Table>

                <Button onClick={updateNumber}>
                    Confirm
                </Button>
                                
                <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
                    Cancel
                </Button>       
                
            </Main>
        </div>

    );
}

export default NumberChangeConfirmation;
