import React, { useState } from "react";
//import { useNavigate } from "react-router-dom";
import jq from "jquery";
import { Main, Fieldset, InputField, Button } from "govuk-react";

function StaffLoginForm() {
  //const navigate = useNavigate();

  const [empId, setEmpId] = useState("");
  const [password, setPassword] = useState("");

  const [validEmpId, setValidEmpId] = useState(false);
  const [validPw, setValidPw] = useState(false);

  const empIdHandler = (e) => {
    let input = e.target.value;
    const regex = /^[0-9]+$/;

    if (regex.test(input) && input.length > 3) {
      setEmpId(input);
      setValidEmpId(true);
    } else {
      setValidEmpId(false);
    }
  };

  const submitFormHandler = (e) => {
    e.preventDefault();

    //Retrieve data from db
    jq.ajax({
      url: "http://localhost:4000/stafflogindata.php",
      type: "POST",
      data: {
        empId: empId,
        password: password,
      },
      dataType: "json",
      success: function (response) {
        if (response === "no users") {
          console.error("No users found.");
        } else {
          let empId = response[0].empId;
          let empType = response[0].employeeType;
          console.log(empId);
          console.log(empType);
          localStorage.setItem("empId", empId);
          localStorage.setItem("empType", empType);
          //navigate("/");
        }
      },
      error: function (error) {
        console.log(error);
        // handle error here
      },
    });
  };

  const pwHandler = (e) => {
    let input = e.target.value;
    setPassword(input);
    if (input.length > 0) {
      setValidPw(true);
    } else {
      setValidPw(false);
    }
  };


  return (
    <Main>
      <form  onSubmit={submitFormHandler}>
        <Fieldset>
          <Fieldset.Legend size="LARGE">Staff Login</Fieldset.Legend>
          <InputField
            padding={3}
            onChange={empIdHandler}
            value={empId}
            input={{
              name: "empId",
              type: "text",
            }}
          >
            Username
          </InputField>
          <InputField
            padding={3}
            onChange={pwHandler}
            value={password}
            input={{
              name: "password",
              type: "password",
            }}
          >
            Password
          </InputField>
          <Button type="submit" margin={3} disabled={!validEmpId || !validPw}>
            Continue
          </Button>
        </Fieldset>
      </form>
    </Main>
  );
}

export default StaffLoginForm;
