/**
* Author(s) of this code: 
*
* Joven Manikiza
*/

import { Button, Heading, Main, Paragraph, SectionBreak, Table } from "govuk-react";
import React, { useEffect, useState } from "react";
import { useNavigate,useLocation } from "react-router-dom";
import Navigation from "../../../Navigation";

function AddressChangeConfirmation(){

    const navigate = useNavigate();

     const [data, setData] = useState({});
    const location = useLocation();
  const address = location.state?.address;

  useEffect(() => {
    fetch('http://localhost:4000/getData.php')
      .then(response => response.json())
      .then(data => {
        setData({
        previousAddress: data[0].Postcode,
        });
      })
      .catch(error => console.error(error));
  }, []);

   
  console.log("address:", address); // add this console.log to check the value of address

  const handleConfirmClick = () => {
  console.log("confirm click address:", address); // add this console.log to check the value of address when the confirm button is clicked
  fetch("http://localhost:4000/updateAddress.php", {
    method: "POST",
    credentials: "include",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ newPostcode: address }),
  })
    .then((response) => response.text())
    .then((data) => {
      console.log(data);
      return JSON.parse(data);
    })
    .then((data) => {
      console.log(data);
      navigate("/Profile");
    })
    .catch((error) => {
      console.error(error);
    });
};

    return (
        <div>
            <Navigation pageLink1="/" PageName1="home" pageLink2="/login" PageName2="Login" pageLink3="/NhsNumber" PageName3="Register"/>

            <Main>

                <Heading>
                    Confirm your changes
                </Heading>

                <Heading size="MEDIUM">Previous details were:</Heading>
                
                <SectionBreak></SectionBreak>
                <Table>
                    <Table.Row>
                        <Table.CellHeader>
                            Postcode:
                        </Table.CellHeader>
                        <Table.Cell>
                           {data.previousAddress}
                        </Table.Cell>
                    </Table.Row>
                </Table>
 
                <Heading size="MEDIUM">New details are:</Heading>
                <Table>
                    <Table.Row>
                        <Table.CellHeader>
                            Postcode:
                        </Table.CellHeader>
                        <Table.Cell>
                            {address}
                        </Table.Cell>
                    </Table.Row>

                </Table>
        <Button onClick={handleConfirmClick}>Confirm</Button>
                                
                <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
                    Cancel
                </Button>       
                
            </Main>
        </div>

    );
}

export default AddressChangeConfirmation;
