import React, { useState } from "react";
import { DateField, Main, Button, SectionBreak } from "govuk-react";
import { useNavigate } from "react-router-dom";
import Navigation from "../../../Navigation";

function DOBChangeEnter() {
  const [day, setDay] = useState("");
  const [month, setMonth] = useState("");
  const [year, setYear] = useState("");
  const [valid, setValid] = useState(true);
  const navigate = useNavigate();

  const handleDateChange = (date) => {
    const { day, month, year } = date;

    setDay(day);
    setMonth(month);
    setYear(year);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isNaN(day) || isNaN(month) || isNaN(year)) {
      setValid(false);
      return;
    }

    const parsedDOB = new Date(year, month - 1, day);
    const formattedDOB = `${parsedDOB.getDate()}/${parsedDOB.getMonth() + 1}/${parsedDOB.getFullYear()}`;

    navigate("/DOBChangeConfirmation", { state: { dob: formattedDOB } });
  };

  return (
    <div>
      <Navigation pageLink1="/" PageName1="home" pageLink2="/login" PageName2="Login" pageLink3="/NhsNumber" PageName3="Register"/>

      <Main>
        <form onSubmit={handleSubmit}>
          <DateField
            input={{
              onChange: handleDateChange,
              name: "dob",
            }}
            meta={{
              touched: true,
              error: !valid ? "Please enter a valid date of birth." : undefined,
            }}
            name="dob"
          >
            What is your date of birth?
          </DateField>

          <Button type="submit" disabled={!day || !month || !year}>
            Continue
          </Button>

          <SectionBreak level="SMALL" visible={false} />

          <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
            Cancel
          </Button>
        </form>
      </Main>
    </div>
  );
}

export default DOBChange;
