/**
* Author(s) of this code: 
*
* Wallyson Alves Da Silva
*/
import React, { useState } from "react";
import { Heading, Button, Input, Main, Breadcrumbs } from "govuk-react";
import { useNavigate} from "react-router-dom";


function InputLabel() {

  //used state to save value of the name
  const [name, setName] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    history.push({
      pathname: '/Components/RegisterPageComponents/FirstName',
      state: {value: name}
    })
  }


  return (
    <div>
      <Main>
        <Breadcrumbs>
          <Breadcrumbs.Link href="/">Home Page</Breadcrumbs.Link>
          <Breadcrumbs.Link href="/NhsNumber">NHS Number</Breadcrumbs.Link>
        </Breadcrumbs>

        <form onSubmit={handleSubmit}>
        <Heading>
          What is your name?
          <Input type="text" value={name} onChange={(e) => setName(e.target.value)}/>
        </Heading>
        <Button type="submit" onClick={() => navigate("/Surname")} start>
          Save and Continue
        </Button>
        </form>
      </Main>
    </div>
  );
}

export default InputLabel;
