import { Tabs, Link } from 'govuk-react';
import { useNavigate, useParams } from 'react-router-dom';
import DRTab from './De-Register/DRTab';
import BookTab from './BookAppointment/BookTab';
import ViewTab from './ViewAppointment/ViewTab';
import DoctorTab from './DoctorsAvailable/DoctorTab';
import { useState } from 'react';

function GPTabs() {
  const navigate = useNavigate();
  const { section } = useParams();

  const handleTabClick = (section) => {
    navigate(`/${section}`);
  };

    const [selectedTab, setSelectedTab] = useState(0);

  const handleTabChange = (index) => {
    setSelectedTab(index);
  };

  return (
<div>
        <Tabs>
      <Tabs.Title>Contents</Tabs.Title>
      <Tabs.List>
        <Tabs.Tab
          selected={selectedTab === 0}
          onClick={() => handleTabChange(0)}
        >
          Doctors at GP
        </Tabs.Tab>
        <Tabs.Tab
          selected={selectedTab === 1}
          onClick={() => handleTabChange(1)}
        >
          Book Appointment
        </Tabs.Tab>
        <Tabs.Tab
          selected={selectedTab === 2}
          onClick={() => handleTabChange(2)}
        >
          View/Cancel Appointment
        </Tabs.Tab>
        <Tabs.Tab
          selected={selectedTab === 3}
          onClick={() => handleTabChange(3)}
        >
          De-Register
        </Tabs.Tab>
      </Tabs.List>

      <div hidden={selectedTab !== 0}>Doctors at GP content goes here</div>
      <div hidden={selectedTab !== 1}>Book Appointment content goes here</div>
      <div hidden={selectedTab !== 2}>View/Cancel Appointment content goes here <ViewTab /></div>
            <div hidden={selectedTab !== 3}>
        <Tabs.Panel id="3">
          <ViewTab />
        </Tabs.Panel>
      </div>
      <div hidden={selectedTab !== 3}>De-Register content goes here</div>
    </Tabs>

    <Tabs>
      <Tabs.Title>Contents</Tabs.Title>
      <Tabs.List>
        <Tabs.Tab as={Link} selected={!section} onClick={() => handleTabClick('Doctors-available')}>
          Doctors at GP
        </Tabs.Tab>
        <Tabs.Tab as={Link} selected={section === 'BookTab'} onClick={() => handleTabClick('Book-appointment')}>
          Book Appointment
        </Tabs.Tab>
        <Tabs.Tab as={Link} selected={section === 'ViewTab'} onClick={() => handleTabClick('View-or-cancel-appointments')}>
          View/Cancel Appointment
        </Tabs.Tab>
        <Tabs.Tab as={Link} selected={section === 'DRTab'} onClick={() => handleTabClick('De-register')}>
          De-Register
        </Tabs.Tab>
      </Tabs.List>

      <DoctorTab selected={!section} />
      <BookTab selected={section === 'BookTab'} />
      <ViewTab selected={section === 'ViewTab'}/>
      <DRTab selected={section === 'DRTab'} />
    </Tabs>
    </div>
  );
}

export default GPTabs;
