import { Button } from "govuk-react";
import { CurrentContext } from "../../Pages/Temp";


function MyBtn(props) {

  function getPatientData() {
    fetch('http://localhost:4000/getpatientdata.php', {
	method: 'POST',
	headers: {
		'Content-Type': 'application/x-www-form-urlencoded'
	},
	body: 'nhsNo=1234567890'
})
.then(response => response.json())
.then(data => {
	// Save variables using currentcontext
	let forename = data[0].Forename;
	let surname = data[0].Surname;
	let postcode = data[0].Postcode;
	
})
.catch(error => {
	console.error(error);
});
  }

  return <Button onClick={getPatientData}> Submit </Button>;
}

export default MyBtn;
