/**
* Author(s) of this code: 
*
* Joven Manikiza
*/

import React, { useState } from "react";
import { InputField, Main, Button, SectionBreak } from "govuk-react";
import { useNavigate } from "react-router-dom";
import Navigation from "../../../Navigation";


function AddressChangeEnter(){

    const navigate = useNavigate();
    const [address, setValue] = useState("");

      const handleSubmit = (e) => {
    e.preventDefault();
    navigate("/AddressChangeConfirmation", { state: { address } });
    console.log(address);
  };
    
    return(
        <div>
           <Navigation pageLink1="/" PageName1="home" pageLink2="/login" PageName2="Login" pageLink3="/NhsNumber" PageName3="Register"/>

            <Main>
                
                <InputField
                    hint={<>You'll find this on the last line on your address</>}
                    input={{  name: 'address' }}
                    meta={{
                    touched: true
                    }}>
                        
                        Your new postcode:
                </InputField>

                <Button onClick={() => {navigate("/AddressChangeConfirmation")}}>
                    Continue
                </Button>

                 <SectionBreak level="SMALL" visible={false}/>
                
                <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
                    Cancel
                </Button>

                </Main>
                </div>

    );
}

export default AddressChangeEnter;
