/**
 * Author(s) of this code:
 *
 * Joven Manikiza
 */

import React, { useState } from "react";
import { DateField, Main, Button, SectionBreak } from "govuk-react";
import { useNavigate } from "react-router-dom";
import Navigation from "../../../Navigation";

function DOBChangeEnter() {
  const navigate = useNavigate();
  const [dob, setDOB] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (dob) {
      console.log("dob before processing:", dob);
      const parts = dob.split("/");
      const dateObject = new Date(parts[2], parts[1] - 1, parts[0]); // year, month (0-based), day
      console.log("dateObject after processing:", dateObject);
      const formattedDOB = `${dateObject.getDate()}/${
        dateObject.getMonth() + 1
      }/${dateObject.getFullYear()}`;
      console.log("formattedDOB:", formattedDOB);
      navigate("/DOBChangeConfirmation", { state: { dob: formattedDOB } });
    } else {
      alert("Please enter a valid date of birth.");
    }
  };

  const handleDOBChange = (value) => {
    console.log("value:", value);
    console.log("typeof value:", typeof value);
    setDOB(value);
  };

  return (
    <div>
      <Navigation
        pageLink1="/"
        PageName1="home"
        pageLink2="/login"
        PageName2="Login"
        pageLink3="/NhsNumber"
        PageName3="Register"
      />

      <Main>
        <form onSubmit={handleSubmit}>
          <DateField
            hintText="For example, 31/03/1980"
            input={{
              name: "dob",
              onChange: handleDOBChange,
            }}
            meta={{
              touched: true,
              error: !dob ? "Please enter a valid date of birth." : undefined,
            }}
          >
            What is your date of birth?
          </DateField>

          <Button>Continue</Button>

          <SectionBreak level="SMALL" visible={false} />

          <Button
            onClick={() => navigate("/Profile")}
            buttonColour="GREY"
          >
            Cancel
          </Button>
        </form>
      </Main>
    </div>
  );
}

export default DOBChangeEnter;
