/**
* Author(s) of this code: 
*
* Joven Manikiza
*/

import { Button, Heading, Main, Table, SectionBreak } from "govuk-react";
import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Navigation from "../../../Navigation";

function BookConfirm() {

  const navigate = useNavigate();
  const location = useLocation();
  const selectedDoctor = location.state?.selectedDoctor;
  const selectedTime = location.state?.selectedTime;

  console.log("selectedDoctor:", selectedDoctor);
  console.log("selectedTime:", selectedTime);

  const [doctors, setDoctors] = useState([]);
  const [randomTime, setRandomTime] = useState("");

  useEffect(() => {
    fetch("http://localhost:4000/GPS_Doc_Avail.php")
      .then((response) => response.json())
      .then((data) => {
        setDoctors(
          data.map((doctor) => ({
            ...doctor,
            specialisation: doctor.Specialisation.toUpperCase(),
            surname: doctor.empLName.toUpperCase(),
          }))
        );
      })
      .catch((error) => console.error(error));
  }, []);

  const makeAppointment = () => {
    const data = {
      doctor: selectedDoctor,
      date: new Date(),
      time: randomTime,
    };

    fetch("http://localhost:4000/GPS_Booking_App.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
      credentials: "include",
    });
  };

  const handleClick = () => {
    // Perform any action needed on confirmation
    makeAppointment();
    navigate("/Application");
  };

  useEffect(() => {
    let startTime, endTime;
    if (selectedTime === "Morning") {
      startTime = new Date();
      startTime.setHours(7);
      startTime.setMinutes(0);
      endTime = new Date();
      endTime.setHours(11);
      endTime.setMinutes(45);
    } else if (selectedTime === "Noon") {
      startTime = new Date();
      startTime.setHours(12);
      startTime.setMinutes(0);
      endTime = new Date();
      endTime.setHours(14);
      endTime.setMinutes(0);
    } else if (selectedTime === "Afternoon") {
      startTime = new Date();
      startTime.setHours(14);
      startTime.setMinutes(0);
      endTime = new Date();
      endTime.setHours(19);
      endTime.setMinutes(0);
    } else {
      // Invalid selectedTime
      setRandomTime("");
      return;
    }

    const timeOptions = [];
    let currentTime = new Date(startTime.getTime());
    while (currentTime <= endTime) {
      if (currentTime.getMinutes() % 15 === 0) {
        timeOptions.push(currentTime);
      }
      currentTime = new Date(currentTime.getTime() + 15 * 60000);
    }

    const randomIndex = Math.floor(Math.random() * timeOptions.length);
    setRandomTime(timeOptions[randomIndex].toLocaleTimeString("en-GB", {
      hour: "numeric",
      minute: "numeric",
      hour12: true,
    }));
  }, [selectedTime]);

  return (
    <div>
      <Navigation
        pageLink1="/"
        PageName1="home"
        pageLink2="/login"
        PageName2="Login"
        pageLink3="/NhsNumber"
        PageName3="Register"
      />

      <Main>
        <Heading>Confirm your booking</Heading>

        <Heading size="MEDIUM"></Heading>

<SectionBreak>
          <Table>
            <Table.Row>
              <Table.CellHeader>Doctor</Table.CellHeader>
              <Table.CellHeader>Specialisation</Table.CellHeader>
              <Table.CellHeader>Surname</Table.CellHeader>
            </Table.Row>
            {doctors
              .filter((doctor) => doctor.ID === selectedDoctor)
              .map((doctor) => (
                <Table.Row key={doctor.ID}>
                  <Table.Cell>{`${doctor.empFName} ${doctor.empLName}`}</Table.Cell>
                  <Table.Cell>{doctor.specialisation}</Table.Cell>
                  <Table.Cell>{doctor.surname}</Table.Cell>
                </Table.Row>
              ))}
          </Table>

sql

      <br />

      <p>
        You have selected a {selectedTime} appointment with{" "}
        {doctors
          .filter((doctor) => doctor.ID === selectedDoctor)
          .map((doctor) => `${doctor.empFName} ${doctor.empLName}`)}{" "}
        on{" "}
        {randomTime
          ? new Date().toLocaleDateString("en-GB", {
              weekday: "long",
              year: "numeric",
              month: "long",
              day: "numeric",
            })
          : "-"}
        {randomTime ? ` at ${randomTime}` : null}
        .
      </p>

      <br />

      <Button onClick={handleClick}>Confirm booking</Button>
    </SectionBreak>
  </Main>
</div>

);
}

export default BookConfirm;
