import React, { useState, useEffect } from 'react';
import { Button, H2, MultiChoice, Radio } from 'govuk-react';
import axios from 'axios';

function DRTab() {
  const nhs_number = localStorage.getItem('nhsNo');

  const handleConfirmClick = () => {

          axios.post('http://localhost:4000/GPS_De-Reg.php', { nhs_number: nhs_number })
            .then(response => {
              alert(response.data.message);
              // redirect to login page or do any other necessary actions
            })
            .catch(error => {
              alert(`Error deleting appointments: ${error.message}`);
            });
        }
      
    

  return (
    <div>
      <br />
      <H2>
        De-Register from GP
      </H2>
      <br />

      <br />
      <Button onClick={() => handleConfirmClick()}>Confirm</Button>
    </div>
  );
}

export default DRTab;
