import React, { useState } from "react";
import { DateField, Main, Button, SectionBreak } from "govuk-react";
import { useNavigate } from "react-router-dom";
import Navigation from "../../../Navigation";

function DOBChangeEnter() {
  const [day, setDay] = useState("");
  const [month, setMonth] = useState("");
  const [year, setYear] = useState("");
  const [valid, setValid] = useState(true);
  const navigate = useNavigate();

  const handleDateChange = (date) => {
    const { day, month, year } = date;

    setDay(day);
    setMonth(month);
    setYear(year);

    console.log("day:", day);
    console.log("month:", month);
    console.log("year:", year);
    console.log(day,month,year);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isNaN(day) || isNaN(month) || isNaN(year)) {
      setValid(false);
      return;
    }

    navigate("/DOBChangeConfirmation", {
      state: { day, month, year },
    });
  };


  return (
    <div>
      <Navigation pageLink1="/" PageName1="home" pageLink2="/login" PageName2="Login" pageLink3="/NhsNumber" PageName3="Register"/>

      <Main>
        <form onSubmit={handleSubmit}>
          <DateField
            inputNames={{
              day: "day",
              month: "month",
              year: "year",
            }}
            inputs={{
              day: {
                autoComplete: "bday-day",
                value: day,
                onChange: (e) => setDay(e.target.value),
              },
              month: {
                autoComplete: "bday-month",
                value: month,
                onChange: (e) => setMonth(e.target.value),
              },
              year: {
                autoComplete: "bday-year",
                value: year,
                onChange: (e) => setYear(e.target.value),
              },
            }}
          >
            What is your date of birth?
          </DateField>

          <Button type="submit" disabled={!day || !month || !year}>
            Continue
          </Button>

          <SectionBreak level="SMALL" visible={false} />

          <SectionBreak level="SMALL" visible={false} />

          <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
            Cancel
          </Button>
        </form>
      </Main>
    </div>
  );
}

export default DOBChangeEnter;
