/**
* Author(s) of this code: 
*
* Joven Manikiza
*/

import { Button, Heading, Main, Paragraph, SectionBreak, Table } from "govuk-react";
import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Navigation from "../../../Navigation";

function AddressChangeConfirmation() {
  const navigate = useNavigate();
  const location = useLocation();
  const newAddress = location.state.address;

  const [previousAddress, setPreviousAddress] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  useEffect(() => {
    fetch("http://localhost:4000/getData.php")
      .then((response) => response.json())
      .then((data) => {
        setPreviousAddress(data[0].Postcode);
      })
      .catch((error) => console.error(error));
  }, []);

const handleConfirmClick = () => {
  setIsUpdating(true);
  fetch("http://localhost:4000/updateAddress.php", {
    method: "POST",
      credentials: 'include',
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ address: newAddress }),
  })
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
      setIsUpdating(false);
      navigate("/Application");
    })
    .catch((error) => {
      console.error(error);
      setIsUpdating(false);
    });
};

  return (
    <div>
      <Navigation
        pageLink1="/"
        PageName1="home"
        pageLink2="/login"
        PageName2="Login"
        pageLink3="/NhsNumber"
        PageName3="Register"
      />

      <Main>
        <Heading>Confirm your changes</Heading>

        <Heading size="MEDIUM">Previous details were:</Heading>

        <SectionBreak />

        <Table>
          <Table.Row>
            <Table.CellHeader>Postcode:</Table.CellHeader>
            <Table.Cell>{previousAddress}</Table.Cell>
          </Table.Row>
        </Table>

        <Heading size="MEDIUM">New details are:</Heading>

        <Table>
          <Table.Row>
            <Table.CellHeader>Postcode:</Table.CellHeader>
            <Table.Cell>{newAddress}</Table.Cell>
          </Table.Row>
        </Table>

        <Button onClick={handleConfirmClick} disabled={isUpdating}>
          {isUpdating ? "Updating..." : "Confirm"}
        </Button>

        <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
          Cancel
        </Button>
      </Main>
    </div>
  );
}

export default AddressChangeConfirmation;
