import { Tabs, Link } from 'govuk-react';
import { useNavigate, useParams } from 'react-router-dom';

import DRTab from './De-Register/DRTab';
import BookTab from './BookAppointment/BookTab';
import ViewTab from './ViewAppointment/ViewTab';
import DoctorTab from './DoctorsAvailable/DoctorTab';

function GPTabs() {
  const navigate = useNavigate();
  const { section } = useParams();

  const handleTabClick = (section) => {
    navigate(`/${section}`);
  };

  return (
    <Tabs>
      <Tabs.Title>Contents</Tabs.Title>
      <Tabs.List>
        <Tabs.Tab as={Link} selected={!section} onClick={() => handleTabClick('Doctors-available')}>
          Doctors at GP
        </Tabs.Tab>
        <Tabs.Tab as={Link} selected={section === 'BookTab'} onClick={() => handleTabClick('Book-appointment')}>
          Book Appointment
        </Tabs.Tab>
        <Tabs.Tab as={Link} selected={section === 'ViewTab'} onClick={() => handleTabClick('View-or-cancel-appointments')}>
          View/Cancel Appointment
        </Tabs.Tab>
        <Tabs.Tab as={Link} selected={section === 'DRTab'} onClick={() => handleTabClick('De-register')}>
          De-Register
        </Tabs.Tab>
      </Tabs.List>

      <DoctorTab selected={!section} />
      <BookTab selected={section === 'BookTab'} />
      <ViewTab selected={section === 'ViewTab'}/>
      <DRTab selected={section === 'DRTab'} />
    </Tabs>
  );
}

export default GPTabs;
