import { useEffect, useState } from "react";
import React from "react";
import { H2, Button, ButtonArrow, DateField, MultiChoice, Radio, Select } from "govuk-react";
import { useNavigate, useLocation } from "react-router-dom";
import BookingContext from "./BookContext";

function BookTab() {

  const [selectedTime, setSelectedTime] = useState("");
  const [selectedDoctor, setSelectedDoctor] = useState("");
  const [isInputFilled, setIsInputFilled] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();
  const [doctors, setDoctors] = useState([]);

    useEffect(() => {
    fetch('http://localhost:4000/GPS_Doc_Avail.php')
      .then(response => response.json())
      .then(data => {
        setDoctors(data.map(doctor => ({
          ...doctor,
          Specialisation: doctor.Specialisation.toUpperCase(),
          empLName: doctor.empLName.toUpperCase(),
        })));
      })
      .catch(error => console.error(error));
  }, []);


  const handleTimeChange = (event) => {
    setSelectedTime(event.target.value);
    console.log('Selected Time:', event.target.value);
    updateIsInputFilled();
  };

  const handleDoctorChange = (event) => {
    setSelectedDoctor(event.target.value);
    console.log('Selected Doctor:', event.target.value);
    updateIsInputFilled();
  };


  function handleButtonClick() {
    if (selectedTime !== "" && selectedDoctor !== "") {
      setIsInputFilled(true);
      navigate("/BookConfirm");
    } else {
      setIsInputFilled(false);
      alert("Please fill all the required fields.");
    }
  }

  function updateIsInputFilled() {
    if (selectedTime !== "" && selectedDoctor !== "") {
      setIsInputFilled(true);
    } else {
      setIsInputFilled(false);
    }
  }

  const doctorOptions = doctors.map((doc, index) => (
    <option value={index} key={index}>
      {"DR. " + doc.last_name + " - " + doc.specialization}
    </option>
  ));

  return (
    <BookingContext.Provider value={{selectedTime,selectedDoctor}}>
    <div>
      <br />
      <H2>Book Appointment</H2>

      <br />
      
      <MultiChoice
        label="What time of day works for you?"
        hint="We will try our best to accommodate your needs"
      >
         <Radio value="Morning" checked={selectedTime === "Morning"} onChange={handleTimeChange}>
          Morning
        </Radio>
        <Radio value="Noon" checked={selectedTime === "Noon"} onChange={handleTimeChange}>
      Noon
    </Radio>
    <Radio value="Afternoon" checked={selectedTime === "Afternoon"} onChange={handleTimeChange}>
      Afternoon
    </Radio>
  </MultiChoice>
  <br />
  <Select label="Select Doctor" onChange={handleDoctorChange}>
    <option value="" disabled selected>
      Choose a doctor
    </option>
    {doctorOptions}
  </Select>
  <br />
  <Button onClick={handleButtonClick} icon={ButtonArrow} disabled={!isInputFilled}>
    Book Appointment
  </Button>
</div>
</BookingContext.Provider>

);
}

export default BookTab;
