*
* Joven Manikiza
*/

import React from "react";
import { Main, Button, ListItem } from "govuk-react";
import { useNavigate, useLocation } from "react-router-dom";
import Navigation from "../../../Navigation";

function AddressChangeConfirmation() {
  const navigate = useNavigate();
  const location = useLocation();

const handleConfirmClick = () => {
  setIsUpdating(true);
  fetch("http://localhost:4000/updateAddress.php", {
    method: "POST",
      credentials: 'include',
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ address: Address }),
  })
    .then((response) => response.text())
    .then((data) => {
      console.log(data);
      return JSON.parse(data);
    })
    .then((data) => {
      console.log(data);
      navigate("/Application");
    })
    .catch((error) => {
      console.error(error);
    });
};

  const handleCancelClick = () => {
    navigate("/Profile");
  };

  return (
    <div>
      <Navigation
        pageLink1="/"
        PageName1="home"
        pageLink2="/login"
        PageName2="Login"
        pageLink3="/NhsNumber"
        PageName3="Register"
      />

      <Main>
        <h1>Confirm your new address</h1>
        <p>Please confirm that this is your new address:</p>
        <ListItem>{location.state.address}</ListItem>
        <Button onClick={handleConfirmClick}>Confirm</Button>
        <Button onClick={() => navigate("/Profile")} buttonColour="GREY">
          Cancel
        </Button>
      </Main>
    </div>
  );
}

export default AddressChangeConfirmation;
